"use strict";
import {parseCoordString, findRootElement, deferredPromise} from '../generics.js';
import {GooglePlatform} from './platform.js';


/**
 * Shows a Google map. This is the base canvas for other visualisation elements.
 * It requires a {@link GooglePlatform} component to handle credentials.
 *
 * @example
 * <google-platform key="..."></google-platform>
 *
 * <google-map platform="google-platform" center="13.5,52.5" zoom="11">
 * </google-map>
 *
 * @see https://developers.google.com/maps/documentation/javascript/
 **/
class GoogleMap extends HTMLElement {

    constructor() {
        super();
        this._ensureBaseStyles();
        this.center = parseCoordString(this.getAttribute("center"));
        this.zoom = parseInt(this.getAttribute("zoom"));
        /** @type {GooglePlatform} */
        this.platform = findRootElement(this, this.getAttribute("platform"), GooglePlatform);
        /** @type {Promise<{map:google.maps.Map}|Error>} */
        this.whenReady = deferredPromise();
    }

    connectedCallback() {
        this.platform.whenReady.then(_ => {
            let map = new google.maps.Map(this, {
              center: this.center,
              zoom: this.zoom,
            });

            this.whenReady.resolve({map:map});
        }).catch(error => this.whenReady.reject(error));
    }

    _ensureBaseStyles() {
        GoogleMap.prototype._ensureBaseStyles = () => {};
        let style = document.createElement('style');
        style.textContent=`google-map { display: block; }`;
        document.head.appendChild(style);
    }
}


/**
 * @external {google.maps.Map} https://developers.google.com/maps/documentation/javascript/reference/map
 */


customElements.define("google-map", GoogleMap);


export { GoogleMap }

import {loadScript, parseString} from '../generics.js';


/**
 * The platform element is required to handle credentials for GOOGLE services.
 *
 * @example
 * <google-platform key="..."></google-platform>
 *
 * <google-router platform="google-platform"
 *      start="lat,lon"
 *      dest="lat,lon">
 * </google-router>
 *
 * @see https://developers.google.com/maps/documentation/
 **/
class GooglePlatform extends HTMLElement {
    constructor() {
        super();

        /** @type {string} */
        this.key = parseString(this.getAttribute("key"), window);

        /** @type {Promise<{service:google.maps.DirectionsService}>} */
        this.whenReady = loadScript(
                "https://maps.googleapis.com/maps/api/js?key=" + this.key
            ).then(_ => {
                return Promise.resolve({ service: new google.maps.DirectionsService() });
            });
    }
}


/**
 * @external {google.maps.DirectionsService} https://developers.google.com/maps/documentation/directions/start
 */


customElements.define("google-platform", GooglePlatform);


export { GooglePlatform }

import {BaseRouter, Request, Response, Route, Leg, Transport, Address, findRootElement, parseString, buildURI, createUID} from '../generics.js';
import {GooglePlatform} from './platform.js';


/**
 * Provides an router element for google directions. It can be referenced in map components.
 *
 * @example
 * <google-router id="google-router"
 *     platform="google-platform"
 *     start="13.30,52.43"
 *     dest="13.76,52.51"
 *     time="2018-12-22T19:17:07">
 * </google-router>
 *
 * @see https://developers.google.com/maps/documentation/directions/start
 **/
class GoogleDirectionsRouter extends BaseRouter {
    constructor() {
        super();
        this.type = "google";

        /** @type {GooglePlatform} */
        this.platform = undefined;
    }

    connectedCallback() {
        this.platform = findRootElement(this, this.getAttribute("platform"), GooglePlatform);
        super.connectedCallback();
    }

    /**
     * returns a Request object
     * @return {Request}
     */
    buildRequest(start, dest, time=undefined) {
        return new Request(this, start, dest, time, {
                mode: this.getAttribute("mode") || "DRIVING",
                alternatives: Boolean(this.getAttribute("max")),
            });
    }

    /**
     * Perform a route request.
     * @async
     * @param {Request} request - route request.
     * @return {Promise<Response|Error>} - route response
     */
    async route(request) {
        return this.platform.whenReady.then(({ service }) => {
            return new Promise((resolve, reject) => {
                service.route({
                    origin: `${request.start.lat},${request.start.lng}`,
                    destination: `${request.dest.lat},${request.dest.lng}`,
                    travelMode: request.mode,
                    transitOptions: {
                        departureTime: new Date(),
                    },
                    provideRouteAlternatives: request.alternatives,
                }, function(res, status) {
                    if (status === 'OK') {
                        resolve(res);
                    } else {
                        reject(status);
                    }
                });
            }).then(res => {
                let routes = res.routes.map((route, index) => {
                    let leg = route.legs[0];
                    let departure = new Address({lat: leg.start_location.lat(), lng: leg.start_location.lng(), name: leg.start_address, type: "addr", time: (leg.departure_time || {}).value || new Date()});
                    let routeArr = new Address({lat: leg.end_location.lat(), lng: leg.end_location.lng(), name: leg.end_address, type: "addr", time: (leg.arrival_time || {}).value || new Date(departure.time.getTime() + leg.duration.value*1000)});
                    let lastTime = new Date(departure.time.getTime());
                    let legs = leg.steps.map((step, index) => {
                        let transit = step.transit || {};
                        let departure = new Address({lat:step.start_location.lat(), lon:step.start_location.lng(), name: step.instructions, time: (transit.departure_time || {}).value || lastTime});
                        lastTime = new Date(lastTime.getTime() + step.duration.value*1000);
                        let arrival = new Address({lat:step.end_location.lat(), lon:step.end_location.lng(), time: (transit.arrival_time || {}).value || lastTime});
                        let transport = buildTransport(step);
                        let geometry = step.path.map(point => [point.lat(), point.lng()]);
                        return new Leg(departure, arrival, transport, geometry, {
                                id:       createUID("g-leg-{uid}"),
                                distance: step.distance.value,
                                summary:  step.instructions,
                            });
                    });
                    return new Route(createUID("g-route-{uid}"), this, departure, routeArr, removeConsecModes(legs));
                });
                return new Response(request, ...routes);
            }).catch(error => new Response(request).setError(error));
        });
    }
}


const LINE_MODE_MAP = {
    WALKING:                "walk",
    COMMUTER_TRAIN:         "metro",
    SUBWAY:                 "subway",
    HEAVY_RAIL:             "train",
    HIGH_SPEED_TRAIN:       "train",
    LONG_DISTANCE_TRAIN:    "train",
    BUS:                    "bus",
}

const TRAVEL_MODE_MAP = {
    BICYCLING:  "bike",
    DRIVING:    "car",
    WALKING:    "walk",
    TRANSIT:    "transit"
}

function buildTransport(step) {
    let tm = TRAVEL_MODE_MAP[step.travel_mode] || step.travel_mode;
    if (tm === "transit") {
        let transit = step.transit || {};
        let line = transit.line || {};
        return new Transport({
                type: LINE_MODE_MAP[step.transit.line.vehicle.type] || step.transit.line.vehicle.type,
                name: line.short_name,
                color: line.color,
                headsign: transit.headsign,
            });
    } else {
        return new Transport({
                type: tm,
                name: tm,
            });
    }
}

function removeConsecModes(legs) {
    let dedup = [legs[0]];
    for (let prev=legs[0], index=1, curr; (curr=legs[index]); index++ ) {
        if (curr.transport.isSame(prev.transport)) {
            prev.extendLeg(curr);
        } else {
            dedup.push(curr);
            prev = curr;
        }
    }
    return dedup;
}


customElements.define("google-router", GoogleDirectionsRouter);


export { GoogleDirectionsRouter }

import {HerePlatform} from './platform.js';
import {HereRouter} from './router.js';
import {HereTransitRouter} from './transit-router.js';
import {HereMap} from './map.js';
import {HereMapRoutes} from './map-routes.js';
import {HereMapRoutePicker} from './map-route-picker.js';


export {
    HerePlatform,
    HereRouter,
    HereTransitRouter,
    HereMap,
    HereMapRoutes,
    HereMapRoutePicker
}

import {findRootElement} from '../generics.js';


/**
 * Adds start+stop map-marker to map and bind them to the router. When markers dragged the router gets updated.
 *
 * @example
 * <here-platform app-id="..." app-code="..."></here-platform>
 * <router id="router"></router>
 *
 * <here-map platform="here-platform" center="13.5,52.5" zoom="11">
 *   <here-map-route-picker router="#router"></here-map-route-picker>
 * </here-map>
 **/
class HereMapRoutePicker extends HTMLElement {
    /**
     * create instance
     */
    constructor() {
        super();
        let mapComp = findRootElement(this, this.getAttribute("map"), customElements.get("here-map"));
        let router = document.querySelector(this.getAttribute("router"));

        mapComp.whenReady.then(({map, behavior}) => {
            let startMarker = new H.map.Marker({lat:0,lng:0}, {icon:new H.map.Icon(TRIP_START_SVG, {anchor: new H.math.Point(21, 50)})});
            startMarker.draggable = true;
            let destMarker = new H.map.Marker({lat:0,lng:0}, {icon:new H.map.Icon(TRIP_DEST_SVG, {anchor: new H.math.Point(21, 50)})});
            destMarker.draggable = true;

            function _dragStartHandler() {
                behavior.disable();
            }

            function _dragEndHandler(ev) {
                let target = ev.target,
                    data = target.getData(),
                    position = target.getPosition();
                behavior.enable();
                router.update(startMarker.getPosition(), destMarker.getPosition());
            }

            function _dragHandler(ev) {
                let marker = ev.target,
                    pointer = ev.currentPointer,
                    newPosition = map.screenToGeo(pointer.viewportX, pointer.viewportY);
                marker.setPosition(newPosition);
            }

            let draggableLayer = new H.map.Group();
            draggableLayer.addEventListener("dragstart", _dragStartHandler, true);
            draggableLayer.addEventListener("drag", _dragHandler, true);
            draggableLayer.addEventListener("dragend", _dragEndHandler, true);
            draggableLayer.addObjects([startMarker, destMarker]);
            map.addObject(draggableLayer);

            // Router
            router.addEventListener("request", ev => {
                let request = ev.detail;
                startMarker.setPosition(request.start);
                destMarker.setPosition(request.dest);
                map.setViewBounds(draggableLayer.getBounds());
            });
            if (router.currentRequest) {
                startMarker.setPosition(router.currentRequest.start);
                destMarker.setPosition(router.currentRequest.dest);
                map.setViewBounds(draggableLayer.getBounds());
            }
        });
    }
}


