import {html, render} from 'https://unpkg.com/lit-html?module';


/**
 * Displays an ugly but copyable string about the current route.
 *
 * @example
 * <router id="router" ...></router>
 *
 * <route-debug router="#router"></route-debug>
 **/
class RouteDebug extends HTMLElement {
    constructor() {
        super();
        this.attachShadow({mode: 'open'});
    }

    connectedCallback() {
        let router = document.querySelector(this.getAttribute("router"));
        if (router) {
            // router events
            router.addEventListener("request", (ev) => {
                this.showDebug(ev.detail);
            });

            // set current state
            router.currentRequest && this.showDebug(router.currentRequest);
        }
    }

    showDebug(request) {
        render(TEMPLATE(request), this.shadowRoot);
    }
}


const TEMPLATE = (request) => html`
        <style>
            :host {
                border: 2px solid blue;
                d_isplay: block;
                background-color: rgba(255, 0, 0, .8);
            }
        </style>
        <div>start=<span>"${request.start.lng},${request.start.lat}"</span> dest=<span>"${request.dest.lng},${request.dest.lat}"</span> time=<span>"${request.time}"</span></div>
    `;


customElements.define("route-debug", RouteDebug);


export { RouteDebug }

import {BaseRouter, parseCoordString, findRootElement} from './generics.js';


/**
 * Contains a clickable route
 *
 * @example
 * <router id="router" ...></router>
 *
 * <route-link router="#router" start="lat,lng" dest="lat,lng" time=".."></route-link>
 **/
class RouteLink extends HTMLElement {
    constructor() {
        super();

        this.addEventListener("click", (ev) => {
            let router = findRootElement(this, this.getAttribute("router"), BaseRouter);
            router.update(parseCoordString(this.getAttribute("start")), parseCoordString(this.getAttribute("dest")), this.getAttribute("time"));
        });
    }
}


customElements.define("route-link", RouteLink);


