template:
    ${render("import", doc)}

${doc.name}


Defined in: ${sourceOf(doc).name}


    ${render("attribute", doc, listExtends(doc), "Extends:", " → ")}
    ${render("attribute", doc, extendedBy(doc), "Extended by:", ", ")}
    ${render("attribute", doc, listImplements(doc), "Implements:", ", ")}
    ${render("attribute", doc, implementedIn(doc), "Implemented by:", ", ")}

    ${render("class-relations", doc, listExtends(doc), "Extends:", " → ")}
    ${render("class-relations", doc, extendedBy(doc), "Extended by:", ", ")}
    ${render("class-relations", doc, listImplements(doc), "Implements:", ", ")}
    ${render("class-relations", doc, implementedIn(doc), "Implemented by:", ", ")}

    ${render("description", doc)}
    ${doc.examples ? doc.examples.map(example => render("example", example)).join("") : ""}
    ${render("class-properties", doc)}
    ${render("class-methods", doc)}
    ${render("class-events", doc)}

template.article:


            ${sourceLink(property, "source")}
            obj
            ${property.access}
            ${property.name}${self.buildPropertySignature(property)}


${JSON.stringify(property, null, 2)}
        ${overrides.length ? overrides.map(target => render("class-method-override", target)) : ''}
        ${inherits ? render("class-method-inherits", self.getParentDoc(property)) : ''}
        ${render("class-deprecation", property, "this property was deprecated")}
        ${render("description", property)}



            ${sourceLink(method, "source")}
            obj
            ${method.static ? "static" : ""}
            ${method.async ? "async" : ""}
            ${method.access}
            ${method.name}${self.buildFunctionSignature(method)}


${JSON.stringify(method, null, 2)}
        ${overrides.length ? overrides.map(target => render("class-method-override", target)) : ''}
        ${inherits ? render("class-method-inherits", self.getParentDoc(method)) : ''}
        ${render("class-deprecation", method, "this method was deprecated")}
        ${render("description", method)}
